    }    protected function getCategory($bitrixId){        if (isset(self::$categories[$bitrixId])) {            return self::$categories[$bitrixId];        } else {            $tree = CmsTree::find()                ->joinWith('relatedElementProperties map')                ->joinWith('relatedElementProperties.property property')                ->andWhere(['property.code' => 'vendorid'])                ->andWhere(['map.value' => $bitrixId])//            ->joinWith('treeType as ttype')//            ->andWhere(['ttype.code' => 'catalog'])                ->limit(1)                ->one();            if ($tree) {                return self::$categories[$bitrixId] = $tree;            }        }        return null;    }    protected function getPriceTypes(){        if (isset(self::$priceTypes)) {            return self::$priceTypes;        } else {            $priceTypes = ShopTypePrice::find()                ->all();            if ($priceTypes) {                foreach ($priceTypes as $type)                    self::$priceTypes[$type->code] = $type;                return self::$priceTypes;            }        }        return null;    }    public function actionInit()    {        $this->createPriceTypes();//        $this->createSimpleItemsProperties();//        $this->createReferenceItemsProperties();        $this->createReferencePropertiesValues();        $this->createColorReferenceProperty();        $this->createColorReferenceValues();//        $this->syncColorSearch();    }    public function actionItems()    {        $this->actionCreateProducts();        $this->actionSyncOffers();////        $this->itemPrices();//        $this->syncActivePrice();//        $this->pricesIndex();////        $this->textProperties();//        $this->lotNumProperty();//        $this->integerProperties();//        $this->elementProperties();//        $this->syncColorRef();////        $this->alsoBuyProperty();//