elect id from ss_web_sync.cms_content_element where bitrix_id in (select id from front2.b_iblock_element where iblock_id=10 and wf_parent_element_id is not null) and content_id=2';        $deleteThis = $this->db->createCommand($query)->queryAll();       $elements = CmsContentElement::find()->andWhere(['id' => $deleteThis])->all();       $counter=0;       $count = count($elements);               foreach ($elements as $cmsContentElement) {            $counter++;////            /**//             * @var $cmsContentElement CmsContentElement//             */            if ($cmsContentElement->delete()) {                Console::updateProgress($counter, $count);                $this->stdout("\tТовар удален: " . $cmsContentElement->id . "\n", Console::FG_GREEN);            } else {                //throw new Exception("Not deleted: " . $cmsContentElement->id);                $this->stdout("\tNot deleted:: " . $cmsContentElement->id . "\n", Console::FG_RED);            }        }        $this->db->createCommand('SET FOREIGN_KEY_CHECKS=1;')->execute();        return true;    }    protected function createPriceTypes()    {        $startTime = time();        $this->stdout("Create Price types\n", Console::FG_CYAN);        try {            $this->fixTableCollation('ss_web_sync.shop_type_price');        } catch (\yii\db\Exception $e) {            $this->stdout("Can't change collation for table ss_web_sync.shop_type_price\n{$e}", Console::FG_RED);            return;        }        $sql = <<<SQL            INSERT INTO ss_web_sync.shop_type_price (id, created_by, updated_by, created_at, updated_at, code, name, priority)            SELECT                CASE WHEN stp.id IS NOT NULL THEN stp.id ELSE NULL END as id,                stp.created_by,                stp.updated_by,                CASE WHEN stp.created_at IS NOT NULL THEN stp.created_at ELSE UNIX_TIMESTAMP() END as created_at,                CASE WHEN stp.updated_at IS NOT NULL THEN stp.updated_at ELSE UNIX_TIMESTAMP() END as updated_at,                TRIM(LEADING 'PRICE_' FROM pa.XML_ID) as code,                pa.VALUE as name,                pa.SORT as priority            from front2.b_iblock_property_enum pa            left join ss_web_sync.shop_type_price stp ON stp.CODE=TRIM(LEADING 'PRICE_' FROM pa.XML_ID)            WHERE pa.PROPERTY_ID=88            ON DUPLICATE KEY UPDATE                updated_at=VALUES(updated_at),                code=VALUES(code),                name=VALUES(name),                priority=VALUES(priority);SQL;        $this->stdout("Syncing price types", Console::FG_YELLOW);        $affected = $this->db->createCommand($sql)->execute();        $this->stdout(" done. Affected {$affected} rows\n", Console::FG_GREEN);        $this->stdout("Done in ".(time()-$startTime)."sec\n\n", Console::FG_GREEN);        return true;    }    protected function createSimpleItemsProperties()    {        $startTime = time();        $this->stdout("Create Product/Offer properties. Types: strings and ints\n", Console::FG_CYAN);        try {            $this->fixTableCollation('ss_web_sync.cms_content_property', 'code', 'utf8_general_ci');        } catch (\yii\db\Exception $e) {            $this->stdout("Can't change collation for table ss_web_sync.cms_content_property\n", Console::FG_RED);            return false;        }        $sql = "            select                ip.id,                (case when ccp.created_at is not null then ccp.created_at else unix_timestamp(ip.timestamp_x) end) as created_at,                unix_timestamp(ip.timestamp_x) as updated_at,                (case when ip.iblock_id=10 then 2 else                    case when ip.iblock_id=11 then 10 else null end                end) as content_id,                ip.code,                ip.active,                ip.name,                ip.hint,                ip.multiple,                (case when ip.multiple='Y' then ip.multiple_cnt else null END) as multiple_cnt,                ip.property_type,                (case when ip.property_type = 'S' and ip.row_count > 1 then 1 else 0 end) as is_textarea,                ip.SORT as priority                from front2.b_iblock_property ip                left join ss_web_sync.cms_content_property ccp ON ccp.code=ip.code                where                     ip.iblock_id in (10,11)                    and ccp.vendor_id is null                    and ip.property_type in ('S','N')                    and ip.code NOT LIKE 'PRICE_%'                    and ip.code NOT LIKE '%_REF'                    and ip.code NOT IN ('".implode("', '", $this->ignoredIblockPropertyCodes)."')";        $bitrixProps = $this->db->createCommand($sql)->queryAll();        if ( !$bitrixProps || count($bitrixProps) < 1 ) {            $this->stdout("No new props at replica\n\n");            return true;        }        $this->stdout("Got new Props: ".count($bitrixProps)."\n\n", Console::FG_GREEN);        $affected = $processed = 0;        $total = count($bitrixProps);        foreach ( $bitrixProps as $prop )        {            ++$affected;            $this->stdout("\n[{$affected} of {$total}] >> ", Console::FG_GREEN);            $this->stdout("Property: {$prop['id']} - {$prop['name']}", Console::FG_YELLOW);            $component = 'skeeks\cms\relatedProperties\propertyTypes\PropertyTypeString';            if ( $prop['property_type'] == 'N' )                $component = 'skeeks\cms\relatedProperties\propertyTypes\PropertyTypeNumber';            if ( $prop['property_type'] == 'S' && $prop['is_textarea'] == 1 )                $component = 'skeeks\cms\relatedProperties\propertyTypes\PropertyTypeText';            /** Завести content element */            try {                $transaction = $this->db->beginTransaction();                $insertContentProp = trim(sprintf("INSERT INTO ss_web_sync.cms_content_property (                            created_by,                            updated_by,                            created_at,                            updated_at,                            content_id,                            name,                            code,                            property_type,                            list_type,                            multiple,                            multiple_cnt,                            component,                            active,                            vendor_id,                            priority                          ) VALUES (                            %d,                            %d,                            %d,                            %d,                            %d,                            %s,                            %s,                            %s,                            %s,                            %s,                            %d,                            %s,                            %s,                            %d,                            %d                          )",                    $this->user->getId(),                    $this->user->getId(),                    $prop['created_at'],                    $prop['updated_at'],                    $prop['content_id'],                    $this->db->quoteValue(str_replace(['"','"'],'', $prop['name'])),                    $this->db->quoteValue($prop['code']),                    $this->db->quoteValue($prop['property_type']),                    $this->db->quoteValue('L'),                    $this->db->quoteValue($prop['multiple']),                    $prop['multiple_cnt'],                    $this->db->quoteValue($component),                    $this->db->quoteValue($prop['active']),                    $prop['id'],                    $prop['priority']                ));                if ( $this->db->createCommand($insertContentProp)->execute() )                    $this->stdout(" -> Ok\n", Console::FG_GREEN);                $transaction->commit();                ++$processed;            } catch (\Exception $e) {                $this->stdout("\tCant create ContentProperty {$e}\n", Console::FG_RED);                $transaction->rollback();                continue;            }        }        $this->stdout("Done in ".(time()-$startTime)."s. Affected {$affected} of {$total} items. Processed: {$processed}\n\n", Console::FG_GREEN);        return true;    }    protected function createReferenceItemsProperties()    {        $startTime = time();        $this->stdout("Create Product/Offer properties. Types: lists and elements\n", Console::FG_CYAN);        try {            $this->fixTableCollation('ss_web_sync.cms_content_property', 'code', 'utf8_general_ci');        } catch (\yii\db\Exception $e) {            $this->stdout("Can't change collation for table ss_web_sync.cms_content_property\n", Console::FG_RED);            return false;        }        try {            $this->fixTableCollation('ss_web_sync.cms_content', 'code', 'utf8_general_ci');        } catch (\yii\db\Exception $e) {            $this->stdout("Can't change collation for table ss_web_sync.cms_content\n", Console::FG_RED);            return false;        }        $sql = "            select                ip.id,                cc.id as ref_content_id,                (case when ccp.created_at is not null then ccp.created_at else unix_timestamp(ip.timestamp_x) end) as created_at,                unix_timestamp(ip.timestamp_x) as updated_at,                (case when ip.iblock_id=10 then 2 else                    case when ip.iblock_id=11 then 10 else null end                end) as content_id,                ip.code,                ip.active,                ip.name,                ip.hint,                ip.multiple,                (case when ip.multiple='Y' then ip.multiple_cnt else null END) as multiple_cnt,    