        if ( !$bitrixBroducts || count($bitrixBroducts) < 1 ) {            $this->stdout("No product offers at replica\n\n");            return false;        }        $this->stdout("Got Product offers: ".count($bitrixBroducts), Console::FG_GREEN);        $affected = $processed = 0;        $total = count($bitrixBroducts);        $transaction = $this->db->beginTransaction();        foreach ( $bitrixBroducts as $product ) {            ++$affected;            $this->stdout("\n[{$affected} of {$total}] >> ", Console::FG_GREEN);            $this->stdout("ContentElement: {$product['ID']} - {$product['NAME']}", Console::FG_YELLOW);            /** Завести content element */            try {                $insertContentElement = trim(sprintf("INSERT INTO ss_web_sync.cms_content_element ( created_at, created_by, updated_at, updated_by, content_id, name, meta_title, code, active, bitrix_id, priority, parent_content_element_id ) VALUES ( %d, %d, %d, %d, %d, %s, %s, %s, %s, %d, %d, %d ) ON DUPLICATE KEY UPDATE updated_at=VALUES(updated_at), updated_by=VALUES(updated_by), code=VALUES(code), name=VALUES(name), active=VALUES(active), priority=VALUES(priority)",                    strtotime($product['DATE_CREATE']),                    $this->user->id,                    strtotime($product['TIMESTAMP_X']),                    $this->user->id,                    self::SITE_OFFERS_CONTENT_ID,                    $this->db->quoteValue($this->getCleanName($product['NAME'])),                    $this->db->quoteValue($this->getCleanName($product['NAME'])),                    $this->db->quoteValue($product['code'].'-'.$product['ID']),                    $this->db->quoteValue($product['ACTIVE']),                    $product['ID'],                    $product['priority'],                    $product['parent_id']                ));                $insertCommand = $this->db->createCommand($insertContentElement);                if ( is_int($insertCommand->execute()) ) // ON DUPLICATE KEY UPDATE возвращает 0 rows affected                    $this->stdout(" -> Ok", Console::FG_GREEN);                else {                    $this->stdout(" -> NOT OK\n", Console::FG_RED);                    $this->stdout("Query:\n ". $insertCommand->getSql()."\n", Console::FG_YELLOW);                    return false;                }//                $transaction->commit();                ++$processed;            } catch (\Exception $e) {                $this->stdout("\tCant create ContentElement {$e}\n", Console::FG_RED);                $transaction->rollback();                return false;            }        }        $productTransaction = $this->db->beginTransaction();        $this->stdout("\nSync shop_product table ", Console::FG_CYAN);        try {            $sql = trim(sprintf("INSERT INTO ss_web_sync.shop_product (id, created_at, created_by, updated_at, updated_by, product_type)          SELECT id, created_at, created_by, updated_at, updated_by, 'offers' FROM ss_web_sync.cms_content_element WHERE content_id=%d           ON DUPLICATE KEY UPDATE id=VALUES(id)",                self::SITE_OFFERS_CONTENT_ID));            if ($affected = $this->db->createCommand($sql)->execute())                $this->stdout(" done. Affected " . $affected . "\n", Console::FG_GREEN);            $productTransaction->commit();        } catch (\yii\db\Exception $e) {            $this->stdout("\tCant create ContentElement {$e}\n", Console::FG_RED);            $transaction->rollBack();            $productTransaction->rollback();            return false;        }        $transaction->commit();        /** Включить модификации у родительских товаров */        $this->stdout("Enable Offers product_type for parent_items ", Console::FG_CYAN);        try {            $sql = trim(sprintf("update ss_web_sync.shop_product SETproduct_type='offers' where id in (select distinct parent_content_element_id from ss_web_sync.cms_content_element where content_id=%d)",                self::SITE_OFFERS_CONTENT_ID));            if ($affected = $this->db->createCommand($sql)->execute())                $this->stdout(" done. Affected " . $affected . "\n", Console::FG_GREEN);        } catch (\yii\db\Exception $e) {            $this->stdout("\tCant update parent element\n{$e}\n", Console::FG_RED);            return false;        }        $this->stdout("\n\nDone in ".(time()-$startTime)."s. Affected {$affected} of {$total} items. Processed: {$processed}\n\n", Console::FG_GREEN);        return true;    }    public function actionSyncPhotos()    {        $startTime = time();        $this->stdout("Sync Product/offers PHOTOS\n", Console::FG_CYAN);        $query = "                select                    distinct f.ID,                ce.id as element_id,                ce.name,                ce.bitrix_id,                p.code as code,                f.SUBDIR,                f.FILE_NAME,                UNIX_TIMESTAMP(f.TIMESTAMP_X) as updated_at                    from front2.b_iblock_element b                    left join front2.b_iblock_element_property ep ON ep.iblock_element_id=b.id  and ep.iblock_property_id in (86,504)                left join front2.b_iblock_property p ON p.id=ep.iblock_property_id                left join front2.b_file f ON f.id=ep.VALUE                left join ss_web_sync.cms_content_element ce ON ce.bitrix_id=b.id and ce.content_id in (2,10)                left join ss_web_sync.cms_content_element_image ccei ON ccei.content_element_id=ce.id                where                    b.iblock_id in (10,11)                    and ep.iblock_property_id in (86,504)                    and f.FILE_NAME IS NOT NULL                    and ce.id is not null                    and ccei.storage_file_id IS NULL        ";        $bitrixProps = $this->db->createCommand($query)->queryAll();        if ( !$bitrixProps || count($bitrixProps) < 1 ) {            $this->stdout("No photos\n\n");            return false;        }        $this->stdout("Got ".count($bitrixProps)." to insert\n", Console::FG_GREEN);        $affected = $processed = 0;        $total = count($bitrixProps);        foreach ( $bitrixProps as $prop ) {            ++$affected;            $this->stdout("\n[{$affected} of {$total}] >> ", Console::FG_GREEN);            $this->stdout("Product/Offer: {$prop['element_id']} photo {$prop['code']}", Console::FG_YELLOW);            try {                $localFile = new File('/upload/'.$prop['SUBDIR'].'/'.$prop['FILE_NAME']);                if ( $localFile->isExist() === false ) {                    $this->stdout('FILE NOT EXIST: /upload/'.$prop['SUBDIR'].'/'.$prop['FILE_NAME'] . "\n", Console::FG_RED);                    continue;                }//            $localFile = 'https://shopandshow.ru/upload/'.$fileName['subdir'].'/'.$fileName['file_name'];//            $this->stdout('Got filename: '.$localFile->getFileName()."\n", Console::FG_GREEN);//            $this->stdout('Copy to temp : '.'/tmp/'.md5(time().'/upload/'.$fileName['subdir'].'/'.$fileName['file_name']).".".$localFile->getExtension()."\n", Console::FG_GREEN);                $tmpFile = new File('/tmp/'.md5(time().'/upload/'.$prop['SUBDIR'].'/'.$prop['FILE_NAME']).".".$localFile->getExtension());                $localFile->copy($tmpFile);                $cmsContentElement = CmsContentElement::findOne($prop['element_id']);                $file = \Yii::$app->storage->upload($tmpFile, [                    'name' => $cmsContentElement->name,                    'updated_at' => $prop['updated_at'],                    'original_name' => '/upload/'.$prop['SUBDIR'].'/'.$prop['FILE_NAME'],                ]);                if ($prop['code'] == 'MAIN_PHOTO') {                    $cmsContentElement->link('image', $file);                } elseif ($prop['code'] == 'PHOTOS') {                    /** Если у товара еще нет основного фото - прикрепить первое из дополнительных фото на место главного */                    if ( $cmsContentElement->image === null )                        $cmsContentElement->link('image', $file);                    $cmsContentElement->link('images', $file);                }            } catch (\Exception $e) {                $message = 'Not upload image to: ' . $cmsContentElement->id . " ({$tmpFile})";                var_dump($message);                var_dump($e->getMessage());            }        }        $this->stdout("\n\nDone in ".(time()-$startTime)."s. Affected {$affected} of {$total} items. Processed: {$processed}\n\n", Console::FG_GREEN);        return true;    }    protected function itemPrices()    {        $startTime = time();        /** В тупую вставить цены из Битрикса, минуя историю изменения цен */        $query = "                SELECT                    sp.id as product_id,                    CASE                        WHEN price_base.VALUE IS NOT NULL THEN price_base.VALUE                        ELSE parent_price_base.VALUE                    END AS BASE,                    CASE                        WHEN price_base.VALUE IS NOT NULL THEN price_base.VALUE                        ELSE parent_price_base.VALUE                    END AS SHOPANDSHOW,                    CASE                        WHEN price_sale.VALUE IS NOT NULL THEN price_sale.VALUE                        ELSE parent_price_sale.VALUE                    END AS SALE,                    CASE                        WHEN price_today.VALUE IS NOT NULL THEN price_today.VALUE                        ELSE parent_price_base.VALUE                    END AS TODAY,                    CASE                        WHEN price_discounted.VALUE IS NOT NULL THEN price_discounted.VALUE                        ELSE parent_price_discounted.VALUE                    END AS DISCOUNTED                FROM front2.b_iblock_element b                LEFT JOIN front2.b_iblock_element_property bep ON bep.IBLOCK_ELEMENT_ID=b.ID AND bep.IBLOCK_PROPERTY_ID=58                LEFT JOIN front2.b_iblock_element_property parent_price_base ON parent_price_base.IBLOCK_ELEMENT_ID=bep.VALUE AND parent_price_base.IBLOCK_PROPERTY_ID=70                LEFT JOIN front2.b_iblock_element_property parent_price_sale ON parent_price_sale.IBLOCK_ELEMENT_ID=bep.VALUE AND parent_price_sale.IBLOCK_PROPERTY_ID=174                LEFT JOIN front2.b_iblock_element_property parent_price_today ON parent_price_today.IBLOCK_ELEMENT_ID=bep.VALUE AND parent_price_today.IBLOCK_PROPERTY_ID=73                LEFT JOIN front2.b_iblock_element_property parent_price_discounted ON parent_price_discounted.IBLOCK_ELEMENT_ID=bep.VALUE AND parent_price_discounted.IBLOCK_PROPERTY_ID=71                LEFT JOIN front2.b_iblock_element_property price_base ON price_base.IBLOCK_ELEMENT_ID=b.ID AND price_base.IBLOCK_PROPERTY_ID=70                LEFT JOIN front2.b_iblock_element_property price_sale ON price_sale.IBLOCK_ELEMENT_ID=b.ID AND price_sale.IBLOCK_PROPERTY_ID=174                LEFT JOIN front2.b_iblock_element_property price_today ON price_today.IBLOCK_ELEMENT_ID=b.ID AND price_today.IBLOCK_PROPERTY_ID=73                LEFT JOIN front2.b_iblock_element_property price_discounted ON price_discounted.IBLOCK_ELEMENT_ID=b.ID AND price_discounted.IBLOCK_PROPERTY_ID=71                LEFT JOIN front2.b_iblock_element_property bp ON bp.IBLOCK_ELEMENT_ID=b.ID AND bp.IBLOCK_PROPERTY_ID=419                LEFT JOIN ss_web_sync.cms_content_element ce ON ce.bitrix_id = b.id                LEFT JOIN ss_web_sync.shop_product sp ON sp.id=ce.id                  WHERE b.id in (select bitrix_id from ss_web_sync.cms_content_element e where e.content_id in (2,10))                  AND sp.id IS NOT NULL        ";        $bitrixPrices = $this->db->createCommand($query)->queryAll();        if ( !$bitrixPrices || count($bitrixPrices) < 1 ) {            $this->stdout("No prices\n\n");            // Yii::$app->end();        }        $this->stdout("Got ".count($bitrixPrices)." to insert\n", Console::FG_GREEN);        $affected = $processed = 0;        $total = count($bitrixPrices);        $priceTypes = $this->getPriceTypes();        if ( count($priceTypes) < 1 ) {            $this->stdout("No prices types configured!\n", Console::FG_RED);            return false;        }        foreach ( $priceTypes as $type )            print_r($type->attributes);        foreach ( $bitrixPrices as $price ) {            ++$affected;            $this->stdout("\n[{$affected} of {$total}] >> ", Console::FG_GREEN);            $this->stdout("Product/Offer: {$price['product_id']} \n\tBASE\t\t{$price['BASE']}\n\tSHOPANDSHOW\t{$price['SHOPANDSHOW']}\n\tTODAY\t\t{$price['TODAY']}\n\tSALE\t\t{$price['SALE']}\n\tDISCOUNTED\t{$price['DISCOUNTED']}\t", Console::FG_YELLOW);            $insertPrices = "INSERT INTO ss_web_sync.shop_product_price (created_at, updated_at, product_id, type_price_id, price, currency_code) VALUES ";            foreach ( $priceTypes as $code => $type ) {                if ( ! array_key_exists($code, $price) )                    continue;                /** БАЗОВАЯ ЦЕНА = БАЗОВАЯ ЦЕНА * 1.2 */                if ( $code == 'BASE' || $code == 'SHOPANDSHOW' )                    $price[$code] = round($price[$code] * 1.2);                if ( reset($priceTypes) !== $type )                    $insertPrices .= ",";                $insertPrices .= sprintf(                    "(UNIX_TIMESTAMP(),UNIX_TIMESTAMP(), %d, %d, %d, 'RUB')",                    $price['product_id'],                    $type->id,                    filter_var($price[$code], FILTER_SANITIZE_NUMBER_INT)                );            }            $insertPrices .= " ON DUPLICATE KEY UPDATE updated_at=VALUES(updated_at), price=VALUES(price)";            try {                $transaction = $this->db->beginTransaction();                if ( $this->db->createCommand($insertPrices)->execute() )                    $this->stdout(" -> Ok\n", Console::FG_GREEN);                $transaction->commit();                ++$processed;            } catch (\Exception $e) {                $this->stdout("\tCant set prices {$e}\n", Console::FG_RED);                $transaction->rollback();                Yii::$app->end();            }        }        $this->stdout("\n\nDone in ".(time()-$startTime)."s. Affected {$affected} of {$total} items. Processed: {$processed}\n\n", Console::FG_GREEN);        return true;    }    protected function syncActivePrice()    {        $startTime = time();        $this->stdout("Reload Product/Offer properties - PRICE_ACTIVE\n", Console::FG_CYAN);        $this->db->createCommand("SET sql_mode = '';")->execute();        $insertSql = <<<SQL          INSERT INTO ss_web_sync.cms_content_element_property (id, created_at, updated_at, property_id, element_id, value)          SELECT            ifnull(exist_prop_value.id, 'default') as current_prop_value,            UNIX_TIMESTAMP() as created_at,            UNIX_TIMESTAMP() as updated_at,                cp.id as property_id,                ce.id as element_id,                rep.id as value            FROM front2.b_iblock_element b            LEFT JOIN front2.b_iblock_element_property bep ON bep.IBLOCK_ELEMENT_ID=b.ID AND bep.IBLOCK_PROPERTY_ID=88            LEFT JOIN ss_web_sync.cms_content_property cp ON cp.vendor_id = bep.IBLOCK_PROPERTY_ID            LEFT JOIN ss_web_sync.cms_content_element rep ON rep.bitrix_id=bep.VALUE AND rep.content_id=(select id from ss_web_sync.cms_content where code='PRICE_ACTIVE')              LEFT JOIN ss_web_sync.cms_content_element ce ON ce.bitrix_id = bep.IBLOCK_ELEMENT_ID and ce.content_id=2             LEFT JOIN ss_web_sync.cms_content_element_property exist_prop_value ON exist_prop_value.element_id=ce.id and exist_prop_value.property_id=cp.id            WHERE					bep.VALUE is not null					and ce.id is not null                    and bep.IBLOCK_PROPERTY_ID=88        ON DUPLICATE KEY UPDATE id=VALUES(id), updated_at=VALUES(updated_at), value=VALUES(value)SQL;        $this->stdout("Inserting properties ", Console::FG_YELLOW);        try {            $insertTransaction = $this->db->beginTransaction();            $affected = $this->db->createCommand($insertSql)->execute();            $this->stdout(" done. Affected " . $affected . "\n", Console::FG_GREEN);            $insertTransaction->commit();        } catch (\yii\db\Exception $e) {            $this->stdout(" Error.\n{$e}\n", Console::FG_RED);            $insertTransaction->rollBack();            return false;        }        $this->stdout("Done in ".(time() - $startTime)."s \n", Console::FG_GREEN);        return true;    }    protected function pricesIndex()    {        $startTime = time();        $this->stdout("Rebuilding aggregated prices table (ss_shop_product_prices)\n", Console::FG_CYAN);        try {            $this->fixTableCollation('ss_web_sync.shop_type_price', 'code', 'utf8_general_ci');        } catch (\yii\db\Exception $e) {            $this->stdout("Can't change collation for table ss_web_sync.cms_content_property\n", Console::FG_RED);            return false;        }        $this->db->createCommand("SET sql_mode = '';")->execute();        $this->db->createCommand("SET @base_price_id = (SELECT id FROM `shop_type_price` WHERE `def` = 'Y');")->execute();//        $this->stdout("Delete all entries ", Console::FG_YELLOW);//        $affected = $this->db->createCommand("DELETE FROM ss_web_sync.ss_shop_product_prices WHERE 1=1;")->execute();//        $this->stdout(" done. Affected ".(int) $affected."\n", Console::FG_GREEN);        $insertSql = <<<SQL        INSERT INTO ss_shop_product_prices (product_id, type_price_id, price, min_price, max_price, discount_percent) SELECT *, ROUND(((price - min_price) / price) * 100 ) AS discount_percent FROM ( SELECT product.id AS product_id, COALESCE(stp_offer.id, @base_price_id) AS type_price_id, COALESCE(sp_offer.price, sp_base.price) AS price,    COALESCE(NULLIF(MIN(COALESCE(sp_min_max_offer.price, sp_min_max_product.price)), ''), COALESCE(sp_offer.price, sp_base.price)) AS min_price,     MAX(COALESCE(sp_min_max_offer.price, sp_min_max_product.price)) AS max_price          FROM cms_content_element AS product            LEFT JOIN cms_content_element parent_products ON parent_products.parent_content_element_id = product.id        LEFT JOIN cms_content_element_property price_active_id ON price_active_id.element_id = product.id        AND price_active_id.property_id = (SELECT id FROM cms_content_property WHERE code = 'PRICE_ACTIVE' AND content_id = 2)        LEFT JOIN cms_content_element_property price_active_type ON  price_active_type.element_id = price_active_id.value         AND price_active_type.property_id = (SELECT id FROM cms_content_property WHERE code = 'PRICE_CODE')        LEFT JOIN shop_type_price AS stp_offer ON stp_offer.code = price_active_type.value    LEFT JOIN shop_product_price AS sp_offer ON sp_offer.product_id = parent_products.id AND (sp_offer.type_price_id = stp_offer.id OR sp_offer.type_price_id = @base_price_id)     LEFT JOIN shop_product_price AS sp_base ON sp_base.type_price_id = @base_price_id AND sp_base.product_id = product.id        LEFT JOIN shop_product_price AS sp_min_max_offer ON sp_min_max_offer.product_id IN (parent_products.id)    LEFT JOIN shop_product_price AS sp_min_max_product ON sp_min_max_product.product_id = product.id        WHERE product.content_id IN (2, 10) GROUP BY product.id) t ON DUPLICATE KEY UPDATE type_price_id=VALUES(type_price_id), price=VALUES(price), min_price=VALUES(min_price), max_price=VALUES(max_price), discount_percent=VALUES(discount_percent)SQL;        $this->stdout("Inserting prices ", Console::FG_GREEN);        if ( $affected = $this->db->createCommand($insertSql)->execute() )            $this->stdout(" done. Affected ".$affected."\n", Console::FG_GREEN);        $this->stdout("\n\nDone in ".(time() - $startTime)." \n\n", Console::FG_GREEN);        return;    }    protected function lotNumProperty()    {        $startTime = time();        $this->stdout("Reload Product/Offer properties - LOT_NUM\n", Console::FG_CYAN);        $this->db->createCommand("SET sql_mode = '';")->execute();        $insertSql = <<<SQL          INSERT INTO ss_web_sync.cms_content_element_property (id, created_at, updated_at, property_id, element_id, value)SELECT            ifnull(exist_prop_value.id, 'default') as current_prop_value,            UNIX_TIMESTAMP() as created_at,            UNIX_TIMESTAMP() as updated_at,            cp.id,            ce.id as element_id,            b.code as value                            FROM front2.b_iblock_element b                            LEFT JOIN ss_web_sync.cms_content_property cp ON cp.code='LOT_NUM'                            LEFT JOIN ss_web_sync.cms_content_element ce ON ce.bitrix_id = b.id                            LEFT JOIN ss_web_sync.cms_content_element_property exist_prop_value ON exist_prop_value.element_id=ce.id and exist_prop_value.property_id=cp.id                            LEFT JOIN ss_web_sync.shop_product sp ON sp.id=ce.id                              WHERE b.id in (select bitrix_id from ss_web_sync.cms_content_element e where e.content_id in (2))        ON DUPLICATE KEY UPDATE id=VALUES(id), updated_at=VALUES(updated_at), value=VALUES(value)SQL;        $this->stdout("Inserting properties ", Console::FG_YELLOW);        try {            $insertTransaction = $this->db->beginTransaction();            $affected = $this->db->createCommand($insertSql)->execute();            $this->stdout(" done. Affected " . $affected . "\n", Console::FG_GREEN);            $insertTransaction->commit();        } catch (\yii\