questions/10152904/unserialize-function-unserialize-error-at-offset                 *                 */                $prop['VALUE'] = preg_replace('/\n/', '', $prop['VALUE']);                $prop['VALUE'] = preg_replace_callback ( '/s:(\d+):"(.*?)";/', function($match) {                    return ($match[1] == strlen($match[2])) ? $match[0] : 's:' . strlen($match[2]) . ':"' . $match[2] . '";';                }, $prop['VALUE'] );                try {                    $_ = unserialize($prop['VALUE']);                    if (is_array($_) & array_key_exists('TEXT', $_))                        $_textValue = $_['TEXT'];                } catch (ErrorException $e) {                    $this->stdout("\tBAD SERIALIZED DATA!\n", Console::FG_RED);                    $errors[] = "Product/Offer: ID={$prop['element_id']} property ID={$prop['property_id']} - ".$e->getMessage();                    continue;                }            } elseif ( $prop['USER_TYPE'] === null ) {                $_textValue = $prop['VALUE'];            }            $insertProp = trim(sprintf("INSERT INTO ss_web_sync.cms_content_element_property (                            id,                            created_at,                            updated_at,                            element_id,                            property_id,                            value                          ) VALUES (                            %s,                            UNIX_TIMESTAMP(),                            UNIX_TIMESTAMP(),                            %d,                            %d,                            %s                          ) ON DUPLICATE KEY UPDATE                            id=VALUES(id),                            updated_at=VALUES(updated_at),                            value=VALUES(value)",                $prop['current_prop_value'],                $prop['element_id'],                $prop['property_id'],                (string) $this-