WHERE                    b.id in (select bitrix_id from ss_web_sync.cms_content_element where content_id in (2,10))                    ) t2 ON t2.element_id=t1.id                    set t1.quantity=t2.front_quantity        ";//        $this->stdout($query."\n\n");        $this->stdout("Updating stocks ", Console::FG_YELLOW);        try {            $insertTransaction = $this->db->beginTransaction();            $affected = $this->db->createCommand($query)->execute();            $this->stdout(" done. Affected " . $affected . "\n", Console::FG_GREEN);            $insertTransaction->commit();        } catch (\yii\db\Exception $e) {            $this->stdout(" Error.\n{$e}\n", Console::FG_RED);            $insertTransaction->rollBack();            return false;        }        $this->stdout("\n\nDone in ".(time() - $startTime)."s \n\n", Console::FG_GREEN);        return true;    }    /** Move to ImportHelper */    protected function getCleanName($string){        $string = preg_replace('/^\[[0-9\-]+\]/xi', '', $string);        $string = preg_replace('/\s\(\d+\)$/xi', '', $string);        $string = htmlspecialchars(trim($string));        return $string;    }    protected function fixTableCollation($table, $column = 'code', $collation = 'utf8_unicode_ci')    {        $this->stdout("Altering table {$table} collation to {$collation}\n", Console::FG_YELLOW);        $this->stdout("ALTER TABLE {$table} CHARACTER SET utf8 COLLATE {$collation}\n", Console::FG_GREY );        if ( $this->db->createCommand("ALTER TABLE {$table} CHARACTER SET utf8 COLLATE {$collation}")->execute() )            $this->stdout("Ok\n", Console::FG_GREEN);        $field = $this->db->createCommand("SHOW FIELDS FROM {$table} where Field ='{$column}'")->queryOne();        $field['Type'] = strtoupper($field['Type']);        $field['Null'] = $field['Null'] == 'NO' ? 'NOT NULL' : '';        $this->stdout("Altering table {$table} change {$column} column collation to {$collation}\n", Console::FG_YELLOW);        $this->stdout("ALTER TABLE {$table} CHANGE {$column} {$column} {$field['Type']} CHARACTER SET utf8 COLLATE {$collation} {$field['Null']}\n", Console::FG_GREY );        if ( $this->db->createCommand("ALTER TABLE {$table} CHANGE {$column} {$column} {$field['Type']} CHARACTER SET utf8 COLLATE {$collation} {$field['Null']}")->execute() )                    $this->stdout("Ok\n", Console::FG_GREEN);    }}