<?phpnamespace console\models\imports;use skeeks\cms\models\CmsTree;use yii\base\Model;/** * @property string $rootPath * @property CmsTree $rootTree * * Class ImportActivePrice * @package common\models\imports */class ImportActivePrice extends Model{    public $_rootPath;    /**     * @return bool|string     */    public function getRootPath()    {        if ($this->_rootPath) {            return $this->_rootPath;        }        $this->_rootPath = \Yii::getAlias('@frontend/web/uploads/imports/all/price_active.csv');        return $this->_rootPath;    }    /**     * @return bool|string     */    public function setRootPath($path)    {        $this->_rootPath = $path;    }    /**     * Количество строк     *     * @return int     */    public function countRows()    {        $counter = 0;        $handle = fopen($this->rootPath, "r");        while (($data = fgetcsv($handle, 0, ",")) !== FALSE) {            $counter++;        }        return $counter;    }}