<?phpnamespace console\models\imports;use skeeks\cms\models\CmsTree;use yii\base\Model;/** * @property string $rootPath * @property CmsTree $rootTree * * Class ImportCategories * @package common\models\imports */class ImportCategories extends Model{    const TREE_TYPE_ID = 5;//    const ROOT_TREE_ID = 9;    const ROOT_TREE_ID = 9;//    const ROOT_TREE_ID = 1432;    public $_rootPath;    /**     * @return bool|string     */    public function getRootPath()    {        if ($this->_rootPath) {            return $this->_rootPath;        }        $this->_rootPath = \Yii::getAlias('@frontend/web/uploads/imports/category/categories.csv');        return $this->_rootPath;    }    /**     * @return bool|string     */    public function setRootPath($path)    {        $this->_rootPath = $path;    }    /**     * @return null|static     */    public function getRootTree()    {        return CmsTree::findOne(static::ROOT_TREE_ID);    }    /**     * Количество строк     *     * @return int     */    public function countRows()    {        $counter = 0;        $handle = fopen($this->rootPath, "r");        while (($data = fgetcsv($handle, 0, ",")) !== FALSE) {            $counter++;        }        return $counter;    }}