<?namespace console\models\sas;use skeeks\cms\models\CmsTree;/** * @property string $column0 //BITRIX_CATEGORY_ID * @property string $column1 //PARENT_ID * @property string $column2 //TITLE * @property string $column3 //ALIAS * @property string $column4 //ACTIVE * @property string $column5 //DESCRIPTION * @property string $column6 //TEXT * @property string $column7 //SORT_ORDER * @property string $column8 //META_TITLE * @property string $column9 //META_DESCRIPTION * * Class CategoryModel * @package console\models\sas */class CategoryModel extends ImportModel{    /**     * @return CmsTree     */    public function getPidCmsTree($id)    {        return CmsTree::find()            ->joinWith('relatedElementProperties map')            ->joinWith('relatedElementProperties.property property')            ->andWhere(['property.code' => 'vendorid'])            ->andWhere(['map.value' => $id])//            ->joinWith('treeType as ttype')//            ->andWhere(['ttype.code' => 'catalog'])            ->one();    }    public function isParent()    {        return !(bool)$this->column1;    }}